mapper.delete:
   
   delete from mission_distribution
   where missionId in 
   
         ${missionId}

   
   delete from mission_progress
   where disId in 
   
         ${disId}

mapper.update:
 update mission_distribution
 set
   remarkContent=#{remarkContent},
   remarkSatrap=#{remarkSatrap} 
where missionId=#{missionId}

 update mission_distribution
 set
   status=#{status} 
where missionId=#{missionId}

mapper.select:
           WITH T AS( 
		    SELECT * FROM sys_users WHERE username=#{username} 
		    UNION ALL 
		    SELECT a.*  FROM sys_users a INNER JOIN T ON a.parentId=T.id 
		)
           
         select top ${pageSize} m.*,d.name department 
         from mission_distribution m 
         left join sys_users u on m.executor=u.username 
         left join sys_depts d on d.id=u.deptId 
         where status in(2,3,4,5,6,7,8) 
         and m.missionId not in (select top ${startIndex} missionId from mission_distribution where status in(2,3,4,5,6,7,8) 
         and 
         (satrap in(SELECT username FROM T) or executor in(SELECT username FROM T))  
		
	         and meeting = #{meeting} 
	     
         
	         and satrap = #{satrap} 
	     
         
	         and executor = #{executor} 
	     
         
	         and status = #{status} 
	     
			
			
			ORDER BY status asc,createdTime desc) 
			 and 
	     (satrap in(SELECT username FROM T) 
	     or executor in(SELECT username FROM T)) 
			
         
	         and meeting = #{meeting} 
	     
         
	         and satrap = #{satrap} 
	     
         
	         and executor = #{executor} 
	     
         
	         and status = #{status} 
	     
	     
         order by m.status asc,m.createdTime desc

            WITH T AS( 
		    SELECT * FROM sys_users WHERE username=#{username} 
		    UNION ALL 
		    SELECT a.*  FROM sys_users a INNER JOIN T ON a.parentId=T.id 
			) 
	        select count(*) 
	        from mission_distribution where status in(2,3,4,5,6,7,8) 
	        and
	        (satrap in(SELECT username FROM T) 
		     or executor in(SELECT username FROM T))

            WITH T AS( 
		    SELECT * FROM sys_users WHERE username=#{username} 
		    UNION ALL 
		    SELECT a.*  FROM sys_users a INNER JOIN T ON a.parentId=T.id 
			) 
           
	        select count(*) 
	        from mission_distribution where status in(2,3,4,5,6,7,8) 
	        and
	        (satrap in(SELECT username FROM T) 
		     or executor in(SELECT username FROM T)) 
	         
	        
	         	and meeting = #{meeting} 
		     
	         
		         and satrap = #{satrap} 
		     
	         
		         and executor = #{executor} 
		     
	         
		         and status = #{status}

select count(*) 
from all_common 
where type=#{type} and details=#{details}
